mapper.select:
SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT,READ_COUNT, MEMBER_NO, MEMBER_NAME, TO_CHAR(BOARD_WRITE_DATE, 'YYYY"년" MM"월" DD"일" HH24:MI:SS') BOARD_WRITE_DATE, TO_CHAR(BOARD_UPDATE_DATE, 'YYYY"년" MM"월" DD"일" HH24:MI:SS') BOARD_UPDATE_DATE,
(SELECT IMG_PATH || IMG_RENAME FROM "BOARD_IMG" WHERE BOARD_NO = ${boardNo} AND IMG_ORDER = 1) THUMBNAIL
FROM "QNABOARD" JOIN "MEMBER" USING(MEMBER_NO) WHERE BOARD_DEL_FL = 'N' AND BOARD_NO = ${boardNo}

mapper.update:
UPDATE "QNABOARD" SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}
